import React from 'react';
import { Card, Button } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import { useAuth } from '../context/AuthContext';

const ProductCard = ({ product }) => {
  const { addToCart } = useCart();
  const { isAuthenticated } = useAuth();

  const handleAddToCart = async (e) => {
    e.preventDefault();
    e.stopPropagation();

    if (!isAuthenticated) {
      alert('Please login to add items to cart');
      return;
    }

    const result = await addToCart(product.id);
    if (result.success) {
      alert('Product added to cart!');
    } else {
      alert(result.error);
    }
  };

  const renderStars = (rating) => {
    const stars = [];
    const fullStars = Math.floor(rating);
    const hasHalfStar = rating % 1 !== 0;

    for (let i = 0; i < fullStars; i++) {
      stars.push(<i key={i} className="bi bi-star-fill text-warning"></i>);
    }

    if (hasHalfStar) {
      stars.push(<i key="half" className="bi bi-star-half text-warning"></i>);
    }

    const emptyStars = 5 - Math.ceil(rating);
    for (let i = 0; i < emptyStars; i++) {
      stars.push(<i key={`empty-${i}`} className="bi bi-star text-warning"></i>);
    }

    return stars;
  };

  return (
    <Card className="product-card h-100 position-relative">
      {product.discount_percentage > 0 && (
        <div className="discount-badge">
          {product.discount_percentage}% OFF
        </div>
      )}

      <Link to={`/product/${product.id}`} className="text-decoration-none">
        <Card.Img 
          variant="top" 
          src={product.image} 
          className="product-image"
          alt={product.name}
        />

        <Card.Body className="d-flex flex-column">
          <Card.Title className="text-dark h6 mb-2" style={{ minHeight: '48px' }}>
            {product.name}
          </Card.Title>

          <div className="mb-2">
            <span className="text-muted small">{product.brand}</span>
          </div>

          <div className="mb-2">
            {renderStars(product.rating)}
            <span className="text-muted ms-2 small">({product.reviews_count})</span>
          </div>

          <div className="mb-3">
            <span className="price-current">${product.price}</span>
            {product.original_price && product.original_price > product.price && (
              <span className="price-original ms-2">${product.original_price}</span>
            )}
          </div>

          <div className="mt-auto">
            <Button 
              variant="primary" 
              size="sm" 
              className="w-100"
              onClick={handleAddToCart}
            >
              <i className="bi bi-cart-plus me-2"></i>
              Add to Cart
            </Button>
          </div>
        </Card.Body>
      </Link>
    </Card>
  );
};

export default ProductCard;
